// src/components/Signup.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useSignupManagerMutation } from "../apis/authApi"; 

const Signup = () => {
  const [userType, setUserType] = useState("employee");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const managerMutation = useSignupManagerMutation();

  const handleUserTypeChange = (e) => {
    setUserType(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("Username:", e.target.elements.username.value);
    console.log("Password:", e.target.elements.password.value);
    console.log("User Type:", userType);


    // try {
    //   const newUser = { username, password };

    //   if (userType === "employee") {
    //     await managerMutation.mutateAsync(newUser);
    //   } else if (userType === "manager") {
    //     await managerMutation.mutateAsync(newUser);
    //   }
    //   console.log(`User signed up as ${userType}`);
    // } catch (error) {
    //   console.error("Signup failed:", error);
    // }
  };







 

  return (
    <div className="flex items-center justify-center h-screen">
    <form
      className="bg-white p-8 shadow-md rounded-md"
      onSubmit={handleSubmit}
    >
      {/* ... (other form fields) */}
      <div className="mb-4">
        <label
          className="block text-gray-700 text-sm font-bold mb-2"
          htmlFor="userType"
        >
          User Type
        </label>
        <select
          className="border rounded-md w-full py-2 px-3"
          id="userType"
          name="userType"
          value={userType}
          onChange={(e) => setUserType(e.target.value)}
        >
          <option value="employee">Employee</option>
          <option value="manager">Manager</option>
        </select>
      </div>
      <button
        className="bg-green-500 text-white rounded-md py-2 px-4 hover:bg-green-600 focus:outline-none focus:shadow-outline-green"
        type="submit"
      >
        Sign up
      </button>
        <p className="mt-4">
          Already have an account?{" "}
          <Link to="/login" className="text-blue-500">
            Log in here
          </Link>
          .
        </p>
      </form>
    </div>
  );
};

export default Signup;
