import {

  signupUser as signupUserUrl,
  loginUser as loginUserUrl,
} from "../services";

import { useState } from 'react';



export const useSignupUser = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
  
    const signupUSer = async (newUser) => {
      setLoading(true);
      try {
        const response = await fetch(signupUser, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newEmployee),
        });
  
        if (!response.ok) {
          throw new Error('Manager signup failed');
        }
  
        // Handle successful signup
        console.log('Manager signed up successfully');
      } catch (error) {
        // Handle signup error
        setError(error.message);
        console.error('Manager signup failed:', error);
      } finally {
        setLoading(false);
      }
    };
  
    return { signupManager, loading, error };
  };


  export const useLoginUser = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
  
    const loginUser = async (existingUser) => {
      setLoading(true);
      try {
        const response = await fetch(loginUserUrl, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newEmployee),
        });
  
        if (!response.ok) {
          throw new Error('User signup failed');
        }
  
        // Handle successful signup
        console.log('User signed up successfully');
      } catch (error) {
        // Handle signup error
        setError(error.message);
        console.error('User signup failed:', error);
      } finally {
        setLoading(false);
      }
    };
  
    return { loginUser, loading, error };
  };
