import {

  signupManager as signupManagerUrl,
  loginManager as loginManagerUrl,
} from "../services";


// src/hooks/useEmployeeSignup.js
import { useState } from 'react';

export const useEmployeeSignup = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const signupEmployee = async (newEmployee) => {
    setLoading(true);
    try {
      const response = await fetch('https://api.example.com/employee/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newEmployee),
      });

      if (!response.ok) {
        throw new Error('Employee signup failed');
      }

      // Handle successful signup
      console.log('Employee signed up successfully');
    } catch (error) {
      // Handle signup error
      setError(error.message);
      console.error('Employee signup failed:', error);
    } finally {
      setLoading(false);
    }
  };

  return { signupEmployee, loading, error };
};


export const useManagerSignup = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
  
    const signupManager = async (newEmployee) => {
      setLoading(true);
      try {
        const response = await fetch('https://api.example.com/employee/signup', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newEmployee),
        });
  
        if (!response.ok) {
          throw new Error('Manager signup failed');
        }
  
        // Handle successful signup
        console.log('Manager signed up successfully');
      } catch (error) {
        // Handle signup error
        setError(error.message);
        console.error('Manager signup failed:', error);
      } finally {
        setLoading(false);
      }
    };
  
    return { signupEmployee, loading, error };
  };
